(function(){document.getElementById('tab-display').style.height=window.innerHeight-document.getElementById('topbar').offsetHeight-document.getElementById('bottombar').offsetHeight+'px';for(let a=0;a<document.getElementsByClassName('tabbutton').length;a++)document.getElementsByClassName('tabbutton')[a].addEventListener('click',function(){for(let c=0;c<document.getElementsByClassName('tabbutton').length;c++)document.getElementsByClassName('tabbutton')[c].classList.remove('selected');this.classList.add('selected');for(let c=0;c<document.getElementsByClassName('tab').length;c++)document.getElementsByClassName('tab')[c].style.display='none';document.getElementById(this.innerText.toLowerCase()+'Section').style.display='block'})})();
